import React, { useState } from 'react';
import { addProduct } from '../services/ProductService';

function AddProduct() {

    const [pName, setPName] = useState('');
    const [pPrice, setPPrice] = useState('');
    const [pCategory, setPCategory] = useState('');

    const handleSubmit = () => {

        const payload = {
            productName: pName,
            productPrice: pPrice,
            category: pCategory
        }

        addProduct(payload).then(resp => alert("Product is saved with id: " + resp.data.productId))
    }

    return (
        <div>
            <div>
                <label>ProductName</label>
                <input type="text" id="pName" name="pName" value={pName} onChange={e => setPName(e.target.value)}></input>
            </div>
            <div>
                <label>ProducPrice</label>
                <input type="text" id="pPrice" name="pPrice" value={pPrice} onChange={e => setPPrice(e.target.value)}></input>
            </div>
            <div>
                <label>Category</label>
                <input type="text" id="pCategory" name="pCategory" value={pCategory} onChange={e => setPCategory(e.target.value)}></input>
            </div>
            <div>
                <button onClick={handleSubmit}>Submit</button>
            </div>
        </div>
    )
}

export default AddProduct;
